package week_3;

public class TwoComputeArea {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		final double PI = 3.14159;
		double radius , area;
		
//		assign a radius
		radius = 20;
		
//		compute area
		area = radius * radius * PI;
		
//		display ans
		System.out.println("The area for th ecircle of radius " + radius +" is " + area);		
	}

}
